#pragma once

#include <cmath>
#include <arbor/mechanism_abi.h>

extern "C" {
  arb_mechanism_type make_arb_allen_catalogue_SK() {
    // Tables
    static arb_field_info globals[] = { { "zTau", "ms", 1, -.000000, .000000 } };
    static arb_size_type n_globals = 1;
    static arb_field_info state_vars[] = { { "z", "", NAN, -.000000, .000000 },
                                           { "zInf", "", NAN, -.000000, .000000 },
                                           { "g", "S / cm2", NAN, -.000000, .000000 } };
    static arb_size_type n_state_vars = 3;
    static arb_field_info parameters[] = { { "gbar", "mho / cm2", .000001, -.000000, .000000 } };
    static arb_size_type n_parameters = 1;
    static arb_ion_info ions[] = { { "k", false, false, false, true, false, false, 0 },
{ "ca", false, false, false, false, false, false, 0 } };
    static arb_size_type n_ions = 2;

    arb_mechanism_type result;
    result.abi_version=ARB_MECH_ABI_VERSION;
    result.fingerprint="<placeholder>";
    result.name="SK";
    result.kind=arb_mechanism_kind_density;
    result.is_linear=false;
    result.has_post_events=false;
    result.globals=globals;
    result.n_globals=n_globals;
    result.ions=ions;
    result.n_ions=n_ions;
    result.state_vars=state_vars;
    result.n_state_vars=n_state_vars;
    result.parameters=parameters;
    result.n_parameters=n_parameters;
    return result;
  }

  arb_mechanism_interface* make_arb_allen_catalogue_SK_interface_multicore();
  arb_mechanism_interface* make_arb_allen_catalogue_SK_interface_gpu();
}
